const { Client } = require('@elastic/elasticsearch');
const client = new Client({ node: '{{node_url}}' });
(async () => {
  await client.index({ index: 'docs', id: '1', document: { title: 'hello' } });
  await client.indices.refresh({ index: 'docs' });
  const result = await client.search({ index: 'docs', query: { match: { title: 'hello' } } });
  console.log('hits', result.hits.hits.length);
})();
