import { Client } from '@elastic/elasticsearch';
const client = new Client({ node: process.env.ES_NODE || '{{es_node}}' });
const index = 'docs_script';
try { await client.indices.delete({ index }); } catch {}
await client.indices.create({ index, mappings: { properties: { text: { type: 'text' }, embedding: { type: 'dense_vector', dims: 3 } } } });
await client.index({ index, document: { text: 'hello', embedding: [0.1,0.2,0.3] } });
await client.index({ index, document: { text: 'fluorite', embedding: [0.2,0.1,0.25] } });
await client.indices.refresh({ index });
const queryVector = [0.1,0.2,0.3];
const res = await client.search({ index, query: { script_score: { query: { match_all: {} }, script: { source: 'cosineSimilarity(params.queryVector, 'embedding') + 1.0', params: { queryVector } } } }, _source: ['text'] });
console.log(res.hits?.hits?.map(h => h._source?.text));
