const { app, BrowserWindow, ipcMain } = require('electron');
const path = require('path');
function createWindow(){ const win = new BrowserWindow({ width: 800, height: 600, webPreferences: { preload: path.join(__dirname, 'preload.js'), contextIsolation: true, nodeIntegration: false } }); win.loadFile('index.html'); }
ipcMain.handle('ping', async (_e, name)=> `Hello ${name}`);
app.whenReady().then(createWindow);
