const { app, BrowserWindow } = require('electron');
async function createWindow(){ const prod = process.env.NODE_ENV === 'production'; const win = new BrowserWindow({ width: 800, height: 600, webPreferences: { sandbox: true, contextIsolation: true, nodeIntegration: false, devTools: !prod } }); await win.loadFile('index.html'); if (!prod) win.webContents.openDevTools(); }
app.whenReady().then(createWindow);
