const url = `https://api.elevenlabs.io/v1/text-to-speech/${process.env.EL_VOICE_ID || '{{voice_id}}'}`;
const res = await fetch(url, { method: 'POST', headers: { 'accept': 'audio/mpeg', 'xi-api-key': process.env.EL_API_KEY, 'content-type': 'application/json' }, body: JSON.stringify({ text: 'Hello from Fluorite MCP' }) });
const buf = Buffer.from(await res.arrayBuffer());
console.log('bytes=', buf.length);
