import React, { useEffect } from 'react';
import { SafeAreaView, Text } from 'react-native';
import * as Notifications from 'expo-notifications';
Notifications.setNotificationHandler({ handleNotification: async ()=> ({ shouldShowAlert: true, shouldPlaySound: false, shouldSetBadge: false }) });
export default function App(){
  useEffect(()=>{ const sub = Notifications.addNotificationReceivedListener((n)=>{ console.log('received', n.request.content); }); return ()=> sub.remove(); },[]);
  return (<SafeAreaView><Text>Background notifications handler set.</Text></SafeAreaView>);
}
