export type RouteData = { route?: string; params?: Record<string,string|number> };
export function notificationToLink(scheme: string, data: RouteData){
  const route = data?.route || ''; const params = data?.params || {};
  const qp = Object.entries(params).map(([k,v])=> `${encodeURIComponent(k)}=${encodeURIComponent(String(v))}`).join('&');
  return scheme.replace(/\/$/, '') + '/' + route + (qp?`?${qp}`:'');
}

