import React, { useEffect, useState } from 'react';
import { SafeAreaView, Text, Button } from 'react-native';
import * as Notifications from 'expo-notifications';
export default function App(){
  const [perm, setPerm] = useState<string>('unknown');
  useEffect(()=>{ (async()=>{ const { status } = await Notifications.requestPermissionsAsync(); setPerm(status); })(); },[]);
  const notify = async ()=>{ await Notifications.scheduleNotificationAsync({ content: { title: 'Hello', body: 'From Expo' }, trigger: null }); };
  return (<SafeAreaView><Text>Permission: {perm}</Text><Button title="Notify" onPress={notify} /></SafeAreaView>);
}
