import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { SafeAreaView, Text } from 'react-native';
const Stack = createNativeStackNavigator();
function Home(){ return (<SafeAreaView><Text>Home</Text></SafeAreaView>); }
function Details(){ return (<SafeAreaView><Text>Details</Text></SafeAreaView>); }
const linking = { prefixes: ['exp://localhost:19000', 'myapp://'], config: { screens: { Home: '', Details: 'details/:id' } } };
export default function App(){
  return (
    <NavigationContainer linking={linking}>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={Home} />
        <Stack.Screen name="Details" component={Details} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
