import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { SafeAreaView, Text, Button } from 'react-native';
function Home({ navigation }: any){ return (<SafeAreaView><Text>Home</Text><Button title="Go Tabs" onPress={()=> navigation.navigate('Tabs')} /></SafeAreaView>); }
function A(){ return (<SafeAreaView><Text>Tab A</Text></SafeAreaView>); }
function B(){ return (<SafeAreaView><Text>Tab B</Text></SafeAreaView>); }
const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();
function Tabs(){
  return (
    <Tab.Navigator>
      <Tab.Screen name="A" component={A} />
      <Tab.Screen name="B" component={B} />
    </Tab.Navigator>
  );
}
export default function App(){
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={Home} />
        <Stack.Screen name="Tabs" component={Tabs} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
