const express = require('express');
const { createNamespace } = require('cls-hooked');
const ns = createNamespace('req');
const app = express();
app.use((req,_res,next)=> ns.run(()=>{ ns.set('id', Date.now()); next(); }));
app.get('/', (_req,res)=> res.send(String(ns.get('id'))));
app.listen(3000);
