const express = require('express');
const cookieParser = require('cookie-parser');
const csurf = require('csurf');
const app = express();
app.use(cookieParser());
app.use(csurf({ cookie: true }));
app.get('/form', (req,res)=> res.send(`<form method="POST" action="/submit"><input type="hidden" name="_csrf" value="${req.csrfToken()}"><button>send</button></form>`));
app.post('/submit', (req,res)=> res.send('ok'));
app.listen(Number('{{port}}'), ()=>console.log('listening'));
