const express = require('express');
const { OpenApiValidator } = require('express-openapi-validator');
const spec = { openapi: '3.0.0', info: { title: 'API', version: '1.0.0' }, paths: { '/hello': { get: { responses: { 200: { description: 'ok' } } } } } };
const app = express();
new OpenApiValidator({ apiSpec: spec }).install(app).then(()=>{ app.get('/hello', (_req,res)=>res.json({ ok: true })); app.listen(3000); });
