const express = require('express');
const { body, validationResult } = require('express-validator');
const app = express();
app.use(express.json());
app.post('/users', body('email').isEmail(), (req,res)=>{ const e=validationResult(req); if(!e.isEmpty()) return res.status(400).json(e.array()); res.json({ ok: true }); });
app.listen(3000);
