const Fastify = require('fastify');
const multipart = require('@fastify/multipart');
const app = Fastify();
app.register(multipart);
app.post('/upload', async (req, reply)=>{ const parts = req.parts(); let c=0; for await (const _p of parts) c++; reply.send({ count:c }); });
app.listen({ port:3000 });
