const Fastify = require('fastify');
const jwt = require('@fastify/jwt');
const app = Fastify();
app.register(jwt, { secret: 'secret' });
app.get('/token', async ()=> app.jwt.sign({ sub: '1' }));
app.get('/me', async (req)=> req.jwtVerify().then(()=> ({ sub: req.user.sub })));
app.listen({ port: 3000 });
