const Fastify = require('fastify');
const jwt = require('@fastify/jwt');
const app = Fastify();
app.register(jwt, { secret: 's' });
app.addHook('onRequest', async (req, reply)=>{ try { await req.jwtVerify(); } catch { reply.code(401).send('unauthorized'); } });
app.get('/', async ()=> 'ok');
app.listen({ port:3000 });
