const Fastify = require('fastify');
const mp = require('@fastify/multipart');
const app = Fastify();
app.register(mp);
app.post('/upload', async (req,res)=>{ const p = await req.file(); const chunks=[]; for await (const c of p.file) chunks.push(c); res.send({ filename: p.filename, bytes: Buffer.concat(chunks).length }); });
app.listen({ port: Number('{{port}}') });
