const Fastify = require('fastify');
const { S3Client, CreateMultipartUploadCommand } = require('@aws-sdk/client-s3');
const s3 = new S3Client({});
const app = Fastify();
app.post('/init', async (_req, res) => { const out = await s3.send(new CreateMultipartUploadCommand({ Bucket: '{{bucket}}', Key: 'upload.bin' })); res.send({ uploadId: out.UploadId }); });
app.listen({ port: Number('{{port}}') }, ()=>console.log('listening'));
