const Fastify = require('fastify');
const { S3Client, UploadPartCommand } = require('@aws-sdk/client-s3');
const { getSignedUrl } = require('@aws-sdk/s3-request-presigner');
const s3 = new S3Client({});
const app = Fastify();
app.get('/sign', async (req,res)=>{ const { uploadId, partNumber } = req.query; const url = await getSignedUrl(s3, new UploadPartCommand({ Bucket: '{{bucket}}', Key: 'upload.bin', UploadId: uploadId, PartNumber: Number(partNumber) }), { expiresIn: 900 }); res.send({ url }); });
app.listen({ port: Number('{{port}}') });
