const Fastify = require('fastify');
const swagger = require('@fastify/swagger');
const app = Fastify();
app.register(swagger, { openapi: { info: { title: 'api', version: '1.0.0' } } });
app.get('/hello', { schema: { response: { 200: { type: 'string' } } } }, async ()=> 'ok');
app.listen({ port: 3000 });
