const http = require('http');
const { IncomingForm } = require('formidable');
http.createServer((req,res)=>{ if(req.method==='POST'){ const f=new IncomingForm(); f.parse(req,(err,fields,files)=>{ res.end(JSON.stringify({ fields, files })); }); } else { res.end('post'); } }).listen(3000);
