import { CloudTasksClient } from '@google-cloud/tasks';
const client = new CloudTasksClient();
const parent = client.queuePath(process.env.GCP_PROJECT || '{{project}}', process.env.GCP_LOCATION || '{{location}}', process.env.GCP_QUEUE || '{{queue}}');
const [res] = await client.createTask({ parent, task: { httpRequest: { httpMethod: 'POST', url: 'https://example.com/hook' } } });
console.log('Task name:', res.name);
