import { CloudTasksClient } from '@google-cloud/tasks';
const c = new CloudTasksClient();
const parent = c.queuePath(process.env.PROJECT||'p','us-central1', process.env.QUEUE||'{{queue}}');
console.log(await c.createTask({ parent, task: { httpRequest: { httpMethod: 'POST', url: 'https://example.com' } } }));
