import { CloudSchedulerClient } from '@google-cloud/scheduler';
const cs = new CloudSchedulerClient();
const parent = cs.locationPath(process.env.PROJECT || '{{project}}', process.env.LOC || '{{location}}');
await cs.createJob({ parent, job: { name: parent + '/jobs/hello', schedule: '*/5 * * * *', httpTarget: { uri: 'https://example.com', httpMethod: 'GET' } } });
console.log('created');
