const { Storage } = require('@google-cloud/storage');
const storage = new Storage();
(async ()=>{ const [url] = await storage.bucket('{{bucket}}').file('{{file}}').getSignedUrl({ action: 'write', version: 'v4', expires: Date.now()+3600_000, contentType: 'application/octet-stream' }); console.log(url); })();
