import { BlobServiceClient } from '@azure/storage-blob';
const conn = process.env.AZURE_STORAGE_CONNECTION_STRING || '';
export const blob = BlobServiceClient.fromConnectionString(conn);
export async function put(container: string, name: string, data: Uint8Array){ const c = blob.getContainerClient(container); const block = c.getBlockBlobClient(name); await block.uploadData(data); }

