import { Storage } from '@google-cloud/storage';
export const gcs = new Storage();
export async function upload(bucket: string, filename: string, buffer: Buffer){ const b = gcs.bucket(bucket); const file = b.file(filename); await file.save(buffer); return file.publicUrl(); }

