import * as Minio from 'minio';
export const minio = new Minio.Client({ endPoint: process.env.MINIO_ENDPOINT || 'localhost', port: parseInt(process.env.MINIO_PORT||'9000',10), useSSL: false, accessKey: process.env.MINIO_ACCESS_KEY||'', secretKey: process.env.MINIO_SECRET_KEY||'' });
export async function put(bucket: string, name: string, data: Buffer){ await minio.putObject(bucket, name, data); }

