import { S3Client, PutObjectCommand, GetObjectCommand } from '@aws-sdk/client-s3';
const region = process.env.AWS_REGION || '{{region}}';
export const s3 = new S3Client({ region });
export async function putObject(bucket: string, key: string, body: Uint8Array){ await s3.send(new PutObjectCommand({ Bucket: bucket, Key: key, Body: body })); }
export async function getObject(bucket: string, key: string){ return s3.send(new GetObjectCommand({ Bucket: bucket, Key: key })); }

