import Stripe from 'stripe';
const apiKey = process.env.STRIPE_API_KEY || '';
export const stripe = new Stripe(apiKey, { apiVersion: '2024-06-20' as any });
export async function createCheckoutSession(priceId: string){
  return stripe.checkout.sessions.create({ mode: 'subscription', line_items: [{ price: priceId, quantity: 1 }], success_url: 'https://example.com/success', cancel_url: 'https://example.com/cancel' });
}

import { NextResponse } from 'next/server';
import Stripe from 'stripe';
const stripe = new Stripe(process.env.STRIPE_API_KEY || '', { apiVersion: '2024-06-20' as any });
const endpointSecret = process.env.STRIPE_WEBHOOK_SECRET || '';
export async function POST(req: Request){
  const sig = req.headers.get('stripe-signature') || '';
  const body = await req.text();
  let event: Stripe.Event;
  try { event = stripe.webhooks.constructEvent(body, sig, endpointSecret); }
  catch (err){ return new NextResponse('invalid signature', { status: 400 }); }
  switch(event.type){ case 'checkout.session.completed': break; default: break; }
  return NextResponse.json({ received: true });
}

