files.template:
name: Vercel Preview Comment

on: [pull_request]

jobs:
  deploy-and-comment:
    runs-on: ubuntu-latest
    steps:
      - uses: actions/checkout@v4
      - id: vercel
        uses: amondnet/vercel-action@v25
        with:
          vercel-token: ${{ secrets.VERCEL_TOKEN }}
          vercel-org-id: ${{ secrets.VERCEL_ORG_ID }}
          vercel-project-id: ${{ secrets.VERCEL_PROJECT_ID }}
          working-directory: .
          vercel-args: '--prebuilt'
      - uses: actions/github-script@v7
        with:
          github-token: ${{ secrets.GITHUB_TOKEN }}
          script: |
            const url = core.getInput('preview-url') || (
              (
                (context.payload as any).deployment?.payload?.url || ''
              )
            );
            const body = `Preview: ${url || 'deployed'}`;
            github.rest.issues.createComment({ owner: context.repo.owner, repo: context.repo.repo, issue_number: context.issue.number, body });
