const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.json({ type: '*/*' }));
app.post('/webhook', (req, res) => {
  const sig = req.headers['x-hub-signature-256'];
  const h = 'sha256=' + crypto.createHmac('sha256', process.env.GITHUB_WEBHOOK_SECRET || 'secret').update(JSON.stringify(req.body)).digest('hex');
  if (sig !== h) return res.status(401).send('invalid');
  res.send('ok');
});
app.listen(3000);
