const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.raw({ type: '*/*' }));
app.post('/github', (req,res)=>{ const sig = req.headers['x-hub-signature-256']; const my = 'sha256=' + crypto.createHmac('sha256', process.env.GH_WEBHOOK_SECRET).update(req.body).digest('hex'); res.status(sig===my?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
