import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient('https://api.github.com/graphql', { headers: { Authorization: `Bearer ${process.env.GITHUB_TOKEN}` } });
const q = gql`{ viewer { login } }`;
try { const r = await client.request(q); console.log(!!r.viewer.login); } catch(e){ console.log('Provide token'); }
