const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.json({ verify: (req, res, buf) => { req.rawBody = buf; } }));
app.post('/webhook', (req,res) => { const sig = req.headers['x-hub-signature-256']; const h = 'sha256=' + crypto.createHmac('sha256', '{{secret}}').update(req.rawBody).digest('hex'); if (!crypto.timingSafeEqual(Buffer.from(h), Buffer.from(sig))) return res.status(401).send('invalid'); res.send('ok'); });
app.listen(Number('{{port}}'), ()=>console.log('listening'));
