files.template:
apiVersion: apps/v1
kind: Deployment
metadata: { name: {{name}} }
spec: { replicas: 1, selector: { matchLabels: { app: {{name}} } }, template: { metadata: { labels: { app: {{name}} } }, spec: { containers: [ { name: app, image: nginx:alpine } ] } } }
---
apiVersion: v1
kind: Service
metadata: { name: {{name}} }
spec: { type: ClusterIP, selector: { app: {{name}} }, ports: [ { port: 80, targetPort: 80 } ] }
