import { google } from 'googleapis';
const auth = new google.auth.GoogleAuth({ scopes: ['https://www.googleapis.com/auth/calendar'] });
const calendar = google.calendar({ version: 'v3', auth });
const start = new Date(Date.now() + 3600e3).toISOString();
const end = new Date(Date.now() + 7200e3).toISOString();
const res = await calendar.events.insert({ calendarId: process.env.CALENDAR_ID || '{{calendar_id}}', requestBody: { summary: 'Test Event', start: { dateTime: start }, end: { dateTime: end } } });
console.log('Event id:', res.data.id);
