files.template:
import { google } from 'googleapis';
import fs from 'node:fs';
const auth = new google.auth.GoogleAuth({ scopes: ['https://www.googleapis.com/auth/drive.file'] });
const drive = google.drive({ version: 'v3', auth });
const fileName = process.env.FILE || '{{filename}}';
fs.writeFileSync(fileName, 'Hello Drive');
const res = await drive.files.create({ requestBody: { name: fileName, mimeType: process.env.MIME || '{{mime}}' }, media: { mimeType: process.env.MIME || '{{mime}}', body: fs.createReadStream(fileName) } });
console.log('Uploaded id:', res.data.id);
