import textToSpeech from '@google-cloud/text-to-speech';
const client = new textToSpeech.TextToSpeechClient();
const [resp] = await client.synthesizeSpeech({ input: { text: 'Hello from Fluorite MCP' }, voice: { languageCode: 'en-US', name: process.env.VOICE_NAME || '{{voice_name}}' }, audioConfig: { audioEncoding: 'MP3' } });
const buf = Buffer.from(resp.audioContent, 'base64');
console.log('bytes=', buf.length);
