const { createYoga, createSchema } = require('graphql-yoga');
const http = require('http');
const yoga = createYoga({ schema: createSchema({ typeDefs: `type Query { hello: String }`, resolvers: { Query: { hello: () => 'world' } } }) });
http.createServer(yoga).listen({{port}}, () => console.log('Yoga running on', {{port}}));
