import Groq from 'groq-sdk';
const groq = new Groq({ apiKey: process.env.GROQ_API_KEY });
const res = await groq.chat.completions.create({ model: '{{groq_model}}', messages: [{ role: 'user', content: 'What time is it? Use the tool.' }], tools: [{ type: 'function', function: { name: 'get_time', description: 'Get current time', parameters: { type: 'object', properties: {} } } }] });
console.log(JSON.stringify(res, null, 2));
