const Hapi = require('@hapi/hapi');
const Joi = require('joi');
(async () => {
  const server = Hapi.server({ port: 3000 });
  server.route({ method: 'POST', path: '/hello', options: { validate: { payload: Joi.object({ name: Joi.string().required() }) } }, handler: (req,h)=>h.response({ ok: true }) });
  await server.start();
})();
