const Hapi = require('@hapi/hapi');
const Joi = require('joi');
(async()=>{ const s=Hapi.server({ port:3000 }); s.route({ method:'GET', path:'/users/{id}', options:{ validate:{ params: Joi.object({ id:Joi.number().integer().required() }) } }, handler: (r,h)=> h.response({ id:r.params.id }) }); await s.start(); })();
