import express from 'express';
import fetch from 'node-fetch';
const app = express();
app.use(express.json());
app.post('/verify', async (req,res)=>{ const token = req.body?.token; const r = await fetch('https://hcaptcha.com/siteverify', { method: 'POST', headers: { 'content-type': 'application/x-www-form-urlencoded' }, body: new URLSearchParams({ secret: process.env.HCAPTCHA_SECRET||'', response: token }) }); const data = await r.json(); res.json(data); });
app.listen(Number('{{port}}'));
