import React from 'react';
import { Dialog } from '@headlessui/react';
export default function HeadlessDialog(){ const [open,setOpen]=React.useState(false); return (<div><button onClick={()=>setOpen(true)}>Open</button><Dialog open={open} onClose={setOpen}><Dialog.Panel><Dialog.Title>Title</Dialog.Title><p>Body</p><button onClick={()=>setOpen(false)}>Close</button></Dialog.Panel></Dialog></div>); }
