files.template:
replicaCount: 1
image: nginx:alpine
service: { type: ClusterIP, port: 80 }

apiVersion: apps/v1
kind: Deployment
metadata: { name: myapp }
spec:
  replicas: {{ .Values.replicaCount }}
  selector: { matchLabels: { app: myapp } }
  template:
    metadata: { labels: { app: myapp } }
    spec:
      containers:
        - name: app
          image: {{ .Values.image }}
          ports: [ { containerPort: 80 } ]

apiVersion: v1
kind: Service
metadata: { name: myapp }
spec:
  type: {{ .Values.service.type }}
  selector: { app: myapp }
  ports: [ { port: {{ .Values.service.port }}, targetPort: 80 } ]
