const express = require('express');
const helmet = require('helmet');
const app = express();
app.use((req,res,next)=>{ res.locals.nonce = Buffer.from(Date.now().toString()).toString('base64'); next(); });
app.use(helmet({ contentSecurityPolicy: { useDefaults: true, directives: { scriptSrc: ["'self'", (req,res) => `'nonce-${res.locals.nonce}'` ] } } }));
app.get('/', (req,res)=> res.send(`<script nonce="${res.locals.nonce}">console.log('ok')</script>`));
app.listen(Number('{{port}}'), ()=>console.log('listening on {{port}}'));
