const express = require('express');
const helmet = require('helmet');
const app = express();
app.use(express.json({ type: ['application/csp-report','application/reports+json','application/json'] }));
app.use(helmet.contentSecurityPolicy({ useDefaults: true, directives: { reportTo: 'csp-endpoint' } }));
app.use(helmet.expectCt({ enforce: true, maxAge: 86400 }));
app.post('/csp-report', (req,res)=>{ console.log('report', req.body); res.sendStatus(204); });
app.get('/', (_req,res)=>res.send('<h1>Hello</h1>'));
app.listen(Number('{{port}}'));
