const url = `https://api-inference.huggingface.co/pipeline/feature-extraction/${process.env.HF_MODEL || '{{model}}'}`;
const res = await fetch(url, { method: 'POST', headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${process.env.HF_TOKEN}` }, body: JSON.stringify({ inputs: 'hello world' }) });
const json = await res.json();
console.log(Array.isArray(json?.[0]) ? json[0].length : json.length);
